import{_ as e,o as t,c as l,a as i}from"./app.41b9aa78.js";const P=JSON.parse('{"title":"\u4EE5\u592A\u574A\u80CC\u540E\u7684\u5BC6\u7801\u5B66","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u692D\u5706\u66F2\u7EBF","slug":"\u692D\u5706\u66F2\u7EBF","link":"#\u692D\u5706\u66F2\u7EBF","children":[]},{"level":2,"title":"\u751F\u6210\u516C\u94A5","slug":"\u751F\u6210\u516C\u94A5","link":"#\u751F\u6210\u516C\u94A5","children":[]},{"level":2,"title":"\u54C8\u5E0C\u51FD\u6570","slug":"\u54C8\u5E0C\u51FD\u6570","link":"#\u54C8\u5E0C\u51FD\u6570","children":[]},{"level":2,"title":"\u4EE5\u592A\u574A\u5730\u5740","slug":"\u4EE5\u592A\u574A\u5730\u5740","link":"#\u4EE5\u592A\u574A\u5730\u5740","children":[]}],"relativePath":"notes/mastering-ethereum/2.md","lastUpdated":1677910941000}'),a={name:"notes/mastering-ethereum/2.md"},r=i('<h1 id="\u4EE5\u592A\u574A\u80CC\u540E\u7684\u5BC6\u7801\u5B66" tabindex="-1">\u4EE5\u592A\u574A\u80CC\u540E\u7684\u5BC6\u7801\u5B66 <a class="header-anchor" href="#\u4EE5\u592A\u574A\u80CC\u540E\u7684\u5BC6\u7801\u5B66" aria-hidden="true">#</a></h1><p><strong>\u9677\u95E8\u51FD\u6570</strong>\uFF1A\u6B63\u5411\u8BA1\u7B97\u5BB9\u6613\uFF0C\u53CD\u5411\u63A8\u5BFC\u56F0\u96BE\uFF0C\u4F46\u662F\u5728\u5F97\u77E5\u4E00\u4E9B\u79C1\u5BC6\u4FE1\u606F\u7684\u60C5\u51B5\u4E0B\uFF0C\u53EF\u4EE5\u8F7B\u6613\u53CD\u5411\u8BA1\u7B97\u3002</p><p><strong>\u79C1\u94A5</strong>\uFF1A\u4E00\u7EC4\u968F\u673A\u83B7\u53D6\u7684\u6570\u5B57\uFF0C\u4EE5\u592A\u574A\u79C1\u94A5\u57FA\u672C\u5C31\u662F\u5728 1 \u5230 2^256 \u4E4B\u95F4\u9009\u62E9\u6570\u5B57\uFF0C\u8981\u4FDD\u8BC1\u9009\u62E9\u7684\u8FC7\u7A0B\u4E0D\u53EF\u9884\u6D4B &amp; \u4E0D\u53EF\u91CD\u590D</p><p><strong>\u516C\u94A5</strong>\uFF1A\u901A\u8FC7\u5BF9\u79C1\u94A5\u4F7F\u7528\u692D\u5706\u66F2\u7EBF\u7684\u4E58\u6CD5\u8FD0\u7B97\u5F97\u51FA\uFF0C\u8FD9\u4E2A\u4E58\u6CD5\u8FD0\u7B97\u57FA\u672C\u4E0D\u53EF\u9006\uFF1A<code>PublicKey = PrivateKey * G</code>\uFF0CG \u662F\u4E00\u4E2A\u5E38\u91CF\u70B9\uFF0C\u79F0\u4E3A\u751F\u6210\u70B9\u3002</p><h2 id="\u692D\u5706\u66F2\u7EBF" tabindex="-1">\u692D\u5706\u66F2\u7EBF <a class="header-anchor" href="#\u692D\u5706\u66F2\u7EBF" aria-hidden="true">#</a></h2><ul><li>\u4EE5\u592A\u574A\u4F7F\u7528 secp256k1 \u692D\u5706\u66F2\u7EBF\u7B97\u6CD5</li><li>\u52A0\u6CD5\uFF1A\u7ED9\u5B9A\u692D\u5706\u66F2\u7EBF\u4E0A\u4E24\u70B9 P1 P2\uFF0C\u692D\u5706\u66F2\u7EBF\u4E0A\u5B58\u5728\u7B2C\u4E09\u4E2A\u70B9 \u6EE1\u8DB3 <code>P3 = P1 + P2</code>\u3002\u7B2C\u4E09\u4E2A\u70B9\u7684\u8BA1\u7B97\u5C31\u662F\u5728\u4E24\u4E2A\u70B9\u4E4B\u95F4\u753B\u4E00\u6761\u76F4\u7EBF\uFF0C\u8FD9\u6761\u7EBF\u4F1A\u4E0E\u692D\u5706\u66F2\u7EBF\u5B58\u5728\u552F\u4E00\u4EA4\u70B9\u3002\u5982\u679C P1 P2 \u662F\u540C\u4E00\u4E2A\u70B9\uFF0C\u90A3\u4E48 P1 P2 \u4E4B\u95F4\u7684\u76F4\u7EBF\u5C31\u662F P1(P2) \u70B9\u7684\u5207\u7EBF\uFF0C\u8FD9\u6761\u5207\u7EBF\u4F1A\u4E0E\u692D\u5706\u66F2\u7EBF\u5B58\u5728\u552F\u4E00\u4EA4\u70B9</li><li>\u65E0\u9650\u8FDC\u70B9\uFF1A\u7C7B\u4F3C\u4E8E\u6570\u5B66\u4E2D\u7684 0\uFF0C\u4E00\u4E9B\u60C5\u51B5\uFF08P1 P2 \u7684 x \u76F8\u540C\uFF0C\u4F46\u662F y \u4E0D\u540C\uFF09\u8FD9\u65F6\u4E24\u70B9\u4E4B\u95F4\u7684\u76F4\u7EBF\u662F\u5782\u76F4\u7684\uFF0C\u8FD9\u79CD\u60C5\u51B5\u4E0B P3 \u5C31\u662F\u65E0\u9650\u8FDC\u70B9</li><li>\u4E58\u6CD5\uFF1A\u7531\u52A0\u6CD5\u884D\u751F\uFF0C\u5BF9\u4E8E\u692D\u5706\u66F2\u7EBF\u4E0A\u7684\u4E00\u70B9 P\uFF0C\u82E5 k \u662F\u6574\u6570\uFF0C\u5219 <code>k * P = P + P + ... + P</code></li></ul><h2 id="\u751F\u6210\u516C\u94A5" tabindex="-1">\u751F\u6210\u516C\u94A5 <a class="header-anchor" href="#\u751F\u6210\u516C\u94A5" aria-hidden="true">#</a></h2><p>\u4F7F\u7528\u692D\u5706\u66F2\u7EBF\u4E0A\u9884\u5148\u5B9A\u4E49\u597D\u7684\u751F\u6210\u70B9 G\uFF0C\u6765\u4EA7\u751F\u7684\u53E6\u4E00\u4E2A\u70B9\u5C31\u662F\u516C\u94A5\uFF1A<code>PublicKey = PrivateKey * G</code>\u3002\u751F\u6210\u70B9\u7531 secp256k1 \u5B9A\u4E49\uFF0C\u5728\u6240\u6709 secp256k1 \u4E2D\uFF0C\u751F\u6210\u70B9\u4FDD\u6301\u4E0D\u53D8\uFF0C\u6240\u6709\u901A\u8FC7\u8BE5\u7B97\u6CD5\u751F\u6210\u7684\u516C\u94A5\u90FD\u4F7F\u7528\u4E86\u8BE5\u751F\u6210\u70B9\u3002\u56E0\u4E3A\u751F\u6210\u70B9\u4E0D\u53D8\uFF0C\u6240\u4EE5\u901A\u8FC7\u540C\u6837\u7684 PrivateKey \u603B\u4F1A\u5F97\u5230\u540C\u6837\u7684 PublicKey\uFF0C\u4E5F\u5C31\u662F\u4E00\u4E2A\u79C1\u94A5\u5BF9\u5E94\u4E00\u4E2A\u5730\u5740\u3002</p><p>\u901A\u8FC7\u8BA1\u7B97\u5F97\u5230\u7684 PublicKey \u4E3A\u4E00\u4E2A\u70B9 <code>PublicKey = (x,y)</code>\uFF0C\u4EE5\u592A\u574A\u91C7\u7528 130 \u4E2A\u5341\u516D\u8FDB\u5236\u5B57\u7B26\u8868\u793A\u516C\u94A5\uFF0C\u8FD9\u662F\u7531 SECG \u53D1\u5E03\u7684\u884C\u4E1A\u6807\u51C6\u7684\u5E8F\u5217\u5316\u65B9\u5F0F\uFF0C\u6807\u51C6\u5B9A\u4E49\u4E86 4 \u79CD\u524D\u7F00\uFF1A</p><table><thead><tr><th style="text-align:center;">\u524D\u7F00</th><th style="text-align:center;">\u542B\u4E49</th><th style="text-align:center;">\u957F\u5EA6(\u5B57\u8282)</th></tr></thead><tbody><tr><td style="text-align:center;">0x00</td><td style="text-align:center;">\u65E0\u7A77\u8FDC\u70B9</td><td style="text-align:center;">1</td></tr><tr><td style="text-align:center;">0x04</td><td style="text-align:center;">\u672A\u538B\u7F29\u70B9</td><td style="text-align:center;">65</td></tr><tr><td style="text-align:center;">0x01</td><td style="text-align:center;">\u5076\u6570 y \u538B\u7F29\u7684\u70B9</td><td style="text-align:center;">33</td></tr><tr><td style="text-align:center;">0x03</td><td style="text-align:center;">\u5947\u6570 y \u538B\u7F29\u7684\u70B9</td><td style="text-align:center;">33</td></tr></tbody></table><p>\u4EE5\u592A\u574A\u4F7F\u7528\u672A\u538B\u7F29\u7684\u516C\u94A5\uFF0C\u4F7F\u7528\u524D\u7F00\u4E3A <code>0x04</code>\uFF0C\u6240\u4EE5\u516C\u94A5\u7F16\u7801\u540E\u7684\u5F62\u6001\u4E3A <code>04 + x + y</code>\u3002</p><h2 id="\u54C8\u5E0C\u51FD\u6570" tabindex="-1">\u54C8\u5E0C\u51FD\u6570 <a class="header-anchor" href="#\u54C8\u5E0C\u51FD\u6570" aria-hidden="true">#</a></h2><p>\u53EF\u4EE5\u5C06\u4EFB\u610F\u957F\u5EA6\u6570\u636E\u6620\u5C04\u6210\u56FA\u5B9A\u957F\u5EA6\u6570\u636E\u7684\u6570\u5B66\u51FD\u6570\uFF0C\u8F93\u5165\u4E3A\u9884\u6620\u5C04 / \u6D88\u606F / \u8F93\u5165\u6570\u636E\uFF0C\u8F93\u51FA\u4E3A\u54C8\u5E0C\u3002</p><p>\u52A0\u5BC6\u54C8\u5E0C\u51FD\u6570\u662F\u4E00\u79CD<em>\u5355\u5411</em>\u7684\u54C8\u5E0C\u51FD\u6570\uFF0C\u5E76\u4E14\u662F \u201C\u591A\u5BF9\u4E00\u201D \u7684\u51FD\u6570\uFF0C\u53D1\u73B0\u5177\u6709\u76F8\u540C\u54C8\u5E0C\u7684\u4E24\u4E2A\u8F93\u5165\u95EE\u9898\u79F0\u4E3A<em>\u54C8\u5E0C\u78B0\u649E\u95EE\u9898</em>\uFF0C\u54C8\u5E0C\u6027\u8D28\u8D8A\u597D\uFF0C\u54C8\u5E0C\u78B0\u649E\u7684\u6982\u7387\u5C31\u8D8A\u5C0F\uFF08\u5BF9\u4E8E\u4EE5\u592A\u574A\u6765\u8BF4\u51E0\u4E4E\u4E0D\u53EF\u80FD\uFF09\u3002\u54C8\u5E0C\u51FD\u6570\u6709\u4EE5\u4E0B\u7279\u6027\uFF1A</p><ul><li>\u786E\u5B9A\u6027\uFF1A\u540C\u6837\u7684\u8F93\u51FA\u603B\u4F1A\u4EA7\u751F\u540C\u6837\u7684\u54C8\u5E0C</li><li>\u53EF\u9A8C\u8BC1\u6027\uFF1A\u8BA1\u7B97\u4EFB\u610F\u8F93\u5165\u4FE1\u606F\u91CF\u7684\u54C8\u5E0C\u8FD0\u7B97\u975E\u5E38\u9AD8\u6548\uFF08\u7EBF\u6027\u7684\u8BA1\u7B97\u6027\u80FD\uFF09</li><li>\u65E0\u5173\u8054\u6027\uFF1A\u5BF9\u4E8E\u8F93\u5165\u7684\u5FAE\u5C0F\u6539\u53D8\uFF0C\u90FD\u4F1A\u4F7F\u8F93\u51FA\u53D1\u751F\u5DE8\u5927\u7684\u53D8\u5316\uFF0C\u5E76\u4E14\u8FD9\u79CD\u53D8\u5316\u548C\u4E4B\u524D\u7684\u6D88\u606F\u6CA1\u6709\u5173\u8054\u6027</li><li>\u4E0D\u53EF\u9006\u6027\uFF1A\u4ECE\u54C8\u5E0C\u53CD\u5411\u8BA1\u7B97\u5F97\u5230\u8F93\u5165\u662F\u4E0D\u73B0\u5B9E\u7684\uFF0C\u53EA\u80FD\u7528\u66B4\u529B\u641C\u7D22</li><li>\u78B0\u649E\u4FDD\u62A4\u6027\uFF1A\u51E0\u4E4E\u4E0D\u53EF\u80FD\u627E\u5230\u5F97\u5230\u540C\u6837\u54C8\u5E0C\u7684\u4E24\u4E2A\u4E0D\u540C\u7684\u8F93\u5165\uFF08\u5BF9\u4E8E\u9632\u6B62\u4F2A\u9020\u4EE5\u592A\u574A\u6570\u5B57\u7B7E\u540D\u5C24\u4E3A\u91CD\u8981\uFF09</li></ul><p>\u5BC6\u7801\u5B66\u54C8\u5E0C\u51FD\u6570\u5E94\u7528\uFF1A</p><ul><li>\u6570\u636E\u6307\u7EB9</li><li>\u6570\u636E\u4E00\u81F4\u6027\uFF08\u9519\u8BEF\u4FA6\u6D4B\uFF09</li><li>\u5DE5\u4F5C\u91CF\u8BC1\u660E</li><li>\u8EAB\u4EFD\u8BA4\u8BC1\uFF08\u5BC6\u7801\u54C8\u5E0C\u548C\u5BC6\u94A5\u5EF6\u4F38\uFF09</li><li>\u4F2A\u968F\u673A\u6570\u751F\u6210</li><li>\u6D88\u606F\u627F\u8BFA</li><li>\u552F\u4E00\u6807\u8BC6\u7B26</li></ul><h2 id="\u4EE5\u592A\u574A\u5730\u5740" tabindex="-1">\u4EE5\u592A\u574A\u5730\u5740 <a class="header-anchor" href="#\u4EE5\u592A\u574A\u5730\u5740" aria-hidden="true">#</a></h2><p>\u4EE5\u592A\u574A\u5730\u5740\u662F\u552F\u4E00\u6807\u8BC6\uFF0C\u4ECE\u516C\u94A5 / \u5408\u7EA6\u901A\u8FC7\u5355\u5411\u54C8\u5E0C\u51FD\u6570 <strong>Keccak-256</strong> \u8BA1\u7B97\u800C\u6765\u3002\u901A\u8FC7 Keccak-256 \u5C06\u7F16\u7801\u540E\u7684\u516C\u94A5\u8FDB\u884C\u54C8\u5E0C\uFF0C\u7136\u540E\u53EA\u4FDD\u7559\u540E 20 \u4F4D\uFF0840 \u4F4D\u5341\u516D\u8FDB\u5236\u5B57\u7B26\uFF09\u4F5C\u4E3A\u4EE5\u592A\u574A\u7684\u5730\u5740\u3002</p><p>\u4EE5\u592A\u574A\u5730\u5740\u4E0D\u672C\u8EAB\u4E0D\u533A\u5206\u5927\u5C0F\u5199\uFF0C\u4F46\u662F\u4E3A\u4E86\u4FDD\u62A4\u5730\u5740\u5B8C\u6574\u6027\uFF0C\u907F\u514D\u4EBA\u4E3A\u7684\u8F93\u5165\u9519\u8BEF\uFF0C\u63D0\u51FA\u4E86 <strong>EIP-55: \u5341\u516D\u8FDB\u5236\u7F16\u7801\u5730\u5740\u7684\u5927\u5C0F\u5199\u6821\u9A8C</strong>\u3002\u4E0D\u652F\u6301 EIP-55 \u7684\u94B1\u5305\u4F1A\u5FFD\u7565\u5927\u5C0F\u5199\uFF0C\u652F\u6301\u7684\u94B1\u5305\u53EF\u4EE5\u901A\u8FC7\u5927\u5C0F\u5199\u6765\u9A8C\u8BC1\u5730\u5740\u7684\u51C6\u786E\u6027\u3002\u5B9E\u73B0\u65B9\u5F0F\u5982\u4E0B\uFF1A</p><ol><li>\u9488\u5BF9\u5168\u5C0F\u5199\u7684\u5730\u5740\u8FDB\u884C\u4E00\u6B21 hash\uFF0C\u4E0D\u5305\u62EC 0x\u3002</li><li>\u4F9D\u6B21\u68C0\u67E5\u5730\u5740\u4E2D\u7684\u5B57\u6BCD\u5BF9\u5E94\u7684 hash\uFF0C\u5982\u679C hash \u7684\u5341\u516D\u8FDB\u5236\u5927\u4E8E/\u7B49\u4E8E 8\uFF0C\u5C31\u628A\u5BF9\u5E94\u7684\u5730\u5740\u5B57\u6BCD\u5927\u5199</li></ol>',21),d=[r];function n(c,s,o,h,p,g){return t(),l("div",null,d)}const y=e(a,[["render",n]]);export{P as __pageData,y as default};
